import React, {useState, Component } from 'react'
import { getTodayDate } from '../../utils'

export class index extends Component {
    constructor(props) {
        super(props);
        this.state = {messageArray: []};
      }


    render() {
        const [nam, setName] = useState();
        const [message, setMessage] = useState();
        const [subj, setSubject] = useState();
        const data = getTodayDate();
        const newMessage = () =>{
            return{
                name: nam,
                message: message,
                subject:subj,
                data: data
            }
        }   

        const setLocalStorage = (e) => {
            e.preventDefault()
           this.state.messageArray.push(newMessage())
           console.log(this.state.messageArray)
            localStorage.setItem("Messages",JSON.stringify(this.state.messageArray));
        }
        return (
            <div>
            <form onSubmit={(e) => setLocalStorage(e)}>
                <div >
                    <input type="name"  id="inputName" value={nam} onChange={(e)=>{setName(e.target.value)}} placeholder="Name" />
                </div>
                <div class="">
                    <input type="text" id="subject" value={subj} onChange={(e)=>{setSubject(e.target.value)}}  placeholder="Subject"/>
                </div>
                <div class="">
                    <textarea class=" textarea"id="message" placeholder="Message"  value={message} onChange={(e)=>{setMessage(e.target.value)}}  rows="6" siz></textarea>
                </div>
                <div class="" style={{textAlign: "center", marginTop: "20px"}} >
                    <button type="submit" class="fill-button bg-green">SEND</button>
                </div>

            </form>

        </div>
        )
    }
}

export default index
