import React, {useState, Component } from 'react'
import { getTodayDate } from '../../utils'

export class index extends Component {
    constructor(props) {
        super(props);
        this.state = {
            messageArray: [],
            nam:null,
            subj:null,
            message:null
        };
      }


    render() {
        // const [nam, setName] = useState();
        // const [message, setMessage] = useState();
        // const [subj, setSubject] = useState();
        const data = getTodayDate();
        const newMessage = () =>{
            return{
                name: this.state.nam,
                message: this.state.message,
                subject:this.state.subj,
                data: data
            }
        }   

        const setLocalStorage = (e) => {
            e.preventDefault()
           this.state.messageArray.push(newMessage())
           console.log(this.state.messageArray)
            localStorage.setItem("Messages",JSON.stringify(this.state.messageArray));
        }
        return (
            <div>
            <form onSubmit={(e) => setLocalStorage(e)}>
                <div >
                    <input type="name"  id="inputName"  onChange={(e)=>{this.setState({nam: e.target.value})}} placeholder="Name" />
                </div>
                <div class="">
                    <input type="text" id="subject"  onChange={(e)=>{this.setState({subj: e.target.value})}} placeholder="Subject"/>
                </div>
                <div class="">
                    <textarea class=" textarea"id="message" placeholder="Message"   onChange={(e)=>{this.setState({message: e.target.value})}} rows="6" siz></textarea>
                </div>
                <div class="" style={{textAlign: "center", marginTop: "20px"}} >
                    <button type="submit" class="fill-button bg-green">SEND</button>
                </div>

            </form>

        </div>
        )
    }
}

export default index
