import React, {useState } from 'react'
import { getTodayDate } from '../../utils'

function Index() {



    const [nam, setName] = useState("newMessage");
    const [message, setMessage] = useState("newMessage");
    const [subj, setSubject] = useState("newSubject");
    const data = getTodayDate();

    const setLocalStorage = (e, nam, subj, message, data) => {
        e.preventDefault();

        const newMessage = () =>{
            return{
               name: this.nam,
               message: message,
                subject:subj,
                data: data
            }
        }
        console.log('NAME: ', newMessage);
        localStorage.setItem("Messages", {newMessage})
    }
    return (
        <div>
            <form onSubmit={(e) => setLocalStorage(e, nam, subj, message, data)}>
                <div >
                    <input type="name"  id="inputName" value={nam} onChange={(e)=>{setName(e.target.value)}} placeholder="Name" />
                </div>
                <div class="">
                    <input type="text" id="subject" value={subj} onChange={(e)=>{setSubject(e.target.value)}}  placeholder="Subject"/>
                </div>
                <div class="">
                    <textarea class=" textarea"id="message" placeholder="Message"  value={message} onChange={(e)=>{setMessage(e.target.value)}}  rows="6" siz></textarea>
                </div>
                <div class="" style={{textAlign: "center", marginTop: "20px"}} >
                    <button type="submit" class="fill-button bg-green">SEND</button>
                </div>

            </form>

        </div>
    )
}
export default Index;
